import React from "react";
import { removeBook, updateBook } from "../utils/api";

class Listitem extends React.Component {
  state = {
    isEditing: false
  };

  deleteBook() {
    removeBook(this.props.book.id);
  }

  editBook() {
    this.setState({ isEditing: true });
    this.forceUpdate();
  }

  saveBook() {
    updateBook(this.props.book.id, "new title", "new author");
  }

  render() {
    return this.state.isEditing ? (
      <li className="list-item list-group-item d-flex align-items-center">
        <input type="text" placeholder={this.props.book.title} />
        <input type="text" placeholder={this.props.book.author} />

        <div className="buttons">
          <button
            type="button"
            className="btn btn-success"
            onClick={this.saveBook.bind(this)}
          >
            Spara
          </button>
          <button
            type="button"
            className="btn btn-danger"
            onClick={this.deleteBook.bind(this)}
          >
            Ta bort
          </button>
        </div>
      </li>
    ) : (
      <li className="list-item list-group-item d-flex align-items-center">
        <strong className="title">{this.props.book.title}</strong>

        <div className="author">{this.props.book.author}</div>

        <div className="buttons">
          <button
            type="button"
            className="btn btn-success"
            onClick={this.editBook.bind(this)}
          >
            Editera
          </button>
          <button
            type="button"
            className="btn btn-danger"
            onClick={this.deleteBook.bind(this)}
          >
            Ta bort
          </button>
        </div>
      </li>
    );
  }
}

export default Listitem;
