import React, { Component } from "react";
import Header from "./components/ui/Header/Header";
import Main from "./components/Main";
import Display from "./components/Display";
import { addBook } from "./utils/api";

const API_KEY = "https://www.forverkliga.se/JavaScript/api/crud.php?requestKey";

class App extends Component {
  state = {
    title: undefined,
    author: undefined,
    status: undefined
  };

  sendBook = async e => {
    e.preventDefault();
    const title = e.target.elements.title.value;
    console.log(title);
    const api_call = await fetch(API_KEY);
    const data = await api_call.json();
    console.log(data);

    console.log("add book");
    addBook(this.state.title, this.state.author);

    /* if (title && author) {
      //console.log(data);
      this.setState({
        temperature: data.main.temp,
        city: data.name,
        country: data.sys.country,
        humidity: data.main.humidity,
        description: data.weather[0].description,
        error: ""
      });
    } else {
      this.setState({
        temperature: undefined,
        city: undefined,
        country: undefined,
        humidity: undefined,
        description: undefined,
        error: "Please enter City and Country"
      });
    } */
  };

  /* componentDidMount() {
    console.log("Get books..");
    fetchBooks().then(response => {
      let status = response.status;
      let data = response.data;

      if (status === "error") {
        localStorage.removeItem("apiKey");
        console.log("apiKey removed");
        requestApiKey();
      }

      //let srt = JSON.stringify(data);
      //console.log("Stutus ApiKey: " + status);
      //console.log("Data: " + srt);
      this.setState({ books: data });
    });
  } */

  render() {
    return (
      <div className="App">
        <Header />
        <Main sendBook={this.sendBook} />
        <Display />
      </div>
    );
  }
}

export default App;
